/* diff highlighting (no syntax highlight) */


/* main diff text color */

.diff-line-pre {
    color: #ccc !important;
}

.diff-table .line-comments {
    border-top: 1px solid #333 !important;
    border-bottom: 1px solid #333 !important;
}

#header-wrapper,
.menu li+li {
    border-bottom: 1px solid #333 !important;
}

